import React from "react";
import { useState } from "react";
import data from "./data.json"
import Card from "./Card";

export default function SearchItems(){

  const [value, setValue] = useState("");

  const onChange = (event) => {
    setValue(event.target.value);
  };

  const onSearch = (searchTerm) => {
    setValue(searchTerm);
    // our api to fetch the search result
    console.log("its working ", searchTerm);
  };

  return (
      <div className="search-container">
        <div className="search-inner">
          <input type="text"  placeholder="search" value={value} onChange={onChange} />
          <button onClick={() => onSearch(value)}> <i className='fas fa-search'></i></button>
        </div>
        <div className="dropdown">
          {data
            .filter((props) => {
              const searchTerm = value.toLowerCase();
              const videos = props.item.videoName.toLowerCase();

              return (
                searchTerm &&
                videoName.startsWith(searchTerm) &&
                videoName !== searchTerm
              );
            })
            .slice(0, 10)
            .map((props) => (
              <div
                onClick={() => onSearch(props.item.videoName)}
                className="dropdown-row"
                key={props.item.videoName}
              >
                {props.item.videoName}
              </div>
            ))}
        </div>
      </div>
  );
}
