import React from 'react'
import "./Header.css"
import {ReactComponent as ReactLogo} from "./logo.svg"
import {useState} from "react"
import data from "./data.json"


export const Header = () => {
    const [value, setValue] = useState("");
    const [submitted, setSubmitted ] = useState(false);

    const onChange = (event) =>{
        setValue(event.target.value)};

    const onSearch = (searchTerm) => {
        setValue(searchTerm)
    };



  return (

    <div className='Header'>

        <div className='Header-left'>
            <i className="fas fa-bars"></i>
            <ReactLogo className='logo'/>
        </div>

        <div className='Header-center'>

            <div className='search'>

             <input type ="text" value={value} onChange={onChange} placeholder='search'  className='search-box'/>
               <button onClick= {()=> onSearch(value)} className='search-btn'>
                  <i className='fas fa-search'></i>
               </button>

              {submitted ? <div className="dropdown">

            {data.map((item)=> (
                <div onClick={()=>onSearch(item.videoName)} className="dropdown-row" key={item.full_name}>{item.videoName}
                </div>)  )}

            </div> :null} 

            <button className='speech-btn'>
                <i className="fa-solid fa-microphone"></i>
            </button>

        </div>


        <div className='Header-right'>
            <button className='create-btn'>
                <i class="fa-solid fa-video"></i>
            </button>
            <button className='notifications-btn'>
                <i className='far fa-bell'></i>
            </button>

        </div>
     </div>   
    </div>

  )
}
